import React, { useEffect } from "react";
import Lottie from "react-lottie";
import * as loading from "./json files/loadcard.json";
import * as loading1 from "./json files/loadcard2.json";

export const Cardloader = () => {
  useEffect(() => {
    setcheck(!check);
  }, [input]);
  const [check, setcheck] = useState(false);
  const defaultOptions = {
    loop: true,
    autoplay: true,
    animationData: loading.default,
    rendererSettings: {
      preserveAspectRatio: "xMidYMid slice",
    },
  };
  const defaultOptions1 = {
    loop: true,
    autoplay: true,
    animationData: loading1.default,
    rendererSettings: {
      preserveAspectRatio: "xMidYMid slice",
    },
  };
  return (
    <div
      className="cardloader"
      style={{ paddingTop: "10vh", paddingBottom: "20vh" }}
    >
      <Lottie
        options={check ? defaultOptions : defaultOptions1}
        height={400}
        width={400}
      />
    </div>
  );
};
